global $ppbpro_addons_data, $ppbpro_tpl;

/**
 * Returns template data if $tpl is set else returns the template keys
 * @param string $tpl Template key
 * @return array|bool Template data
 */
function ppbpro_get_template( $tpl = 0 ) {

	$tpls = ppbpro_get_templates();

	if ( 0 === $tpl ) {
		return array_keys( $tpls );
	} elseif ( ! empty( $tpls[ $tpl ] ) ) {
		return is_string( $tpls[ $tpl ] ) ? json_decode( $tpls[ $tpl ], 'associative_array' ) : $tpls[ $tpl ];
	} else {
		return false;
	}
}

